import React from 'react';

function Quiz({ question, options, selectedOption, onSelectOption }) {
  return (
    <div>
      <h2>{question}</h2>
      <ul>
        {options.map((option, index) => (
          <li
            key={index}
            onClick={() => onSelectOption(index)}
            className={index === selectedOption ? 'selected' : ''}
          >
            {option}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Quiz;
